mapper.select:
/* getCustmrGoodsSearch */ SELECT TC.CUSTMR_CD ,TC.RESP_USER_NM ,TC.CUSTMR_NM ,TC.SUPR_TYPE FROM T_STD_MAST_CUSTMR AS TC WHERE TC.PUR_SALE_TYPE = '2'
AND TC.CUSTMR_GRUP = #{CUSTMR_GRUP}
AND TC.CUSTMR_NM LIKE '%'+#{KEY_WORD}+'%'

mapper.insert:
/* saveAddCustmrGoodsSearch */
DECLARE @GOODS_NO		NVARCHAR(10)

SET @GOODS_NO = (SELECT TOP 1 GOODS_NO
				FROM T_STD_MAST_BCD
				WHERE BCD_CD = #{BCD_CD})

INSERT INTO T_STD_CUSTMR_GOODS( 
		ORGN_DIV_CD
		,ORGN_CD
		,CUSTMR_CD
		,BCD_CD
		,GOODS_NO
		,ORD_TYPE
		,USE_YN
		,CPROGRM
		,CUSER
		,CDATE
		)
VALUES(
		#{ORGN_DIV_CD}
		,#{ORGN_CD}
		,#{CUSTMR_CD}
		,#{BCD_CD}
		,@GOODS_NO
		,'0'
		,#{USE_YN}
		,#{CPROGRM}
		,#{CUSER}
		,NOW()
		)

mapper.update:
/* saveUpdateCustmrGoodsSearch */
UPDATE T_STD_CUSTMR_GOODS
SET USE_YN = #{USE_YN}
	,MPROGRM = #{MPROGRM}
	,MUSER = #{MUSER}
	,MDATE = NOW()
WHERE CUSTMR_CD = #{CUSTMR_CD}
AND BCD_CD = #{BCD_CD}
