mapper.select:
	/* getLoginDto 로그인시도 */
	SELECT CASE WHEN COUNT(1) =  1  THEN '0'
	            ELSE '-1002'
		   END  RESULT_CD
		 , #{login_id} LOGIN_ID
		 , EMP_NO
		 , SITE_DIV_CD
 	  FROM COM_EMP_ACNT A
	 WHERE A.ID  =  #{login_id}
	   AND A.PASSWORD = #{password}

/* getEmpSessionDto */ SELECT
A.EMP_NO emp_no , A.EMP_NM emp_nm , IFNULL(C.ORGN_DELEGATE_CD,'000000') orgn_delegate_cd , (SELECT ORGN_NM FROM COM_ORGN_INFO WHERE ORGN_CD = IFNULL(C.ORGN_DELEGATE_CD,'000000')) orgn_delegate_nm , IFNULL(C.ORGN_CD,'000000') orgn_cd , IFNULL(C.ORGN_NM,'') orgn_nm , A.SEARCHABLE_AUTH_CD searchable_auth_cd , CASE WHEN CA.AUTHOR_CD = '99999' THEN 'true' ELSE 'false' END AS is_manager , CASE WHEN C.ORGN_DELEGATE_CD IN ( SELECT ORGN_CD FROM COM_ORGN_INFO COI INNER JOIN (SELECT * FROM COM_CMMN_CODE_DETAIL WHERE CMMN_CD = 'ORGN_CD' AND DIV1 = 'MK') TEMP ON COI.ORGN_CD = TEMP.CMMN_DETAIL_CD ) THEN 'MK' ELSE 'WINPLUS' END AS board_publish_scope_part , C.ORGN_DELEGATE_CD board_publish_scope_cd , IFNULL(C.ORGN_DIV_CD,'') orgn_div_cd , FN_GET_CMMN_CD_NM('ORGN_DIV_CD', C.ORGN_DIV_CD) orgn_div_nm , A.DLV_DUTY_CD dlv_duty_cd , A.DLV_BSN_CD dlv_bsn_cd , A.MBTLNUM AS mbtlnum , CASE WHEN A.EMAIL IS NULL OR A.EMAIL = '' THEN NULL ELSE A.EMAIL END AS email , A.ADDR AS addr , C.ZIP_NO AS orgn_zip_no , C.ADDR AS orgn_addr , C.ADDR2 AS orgn_addr2 , C.TEL_NO AS orgn_tel_num , CASE WHEN CA.AUTHOR_CD IN ( SELECT CMMN_DETAIL_CD OPER_TEAM FROM COM_CMMN_CODE CM INNER JOIN COM_CMMN_CODE_DETAIL CMD ON CM.CMMN_CD = CMD.CMMN_CD WHERE CM.CMMN_CD = 'OPER_TEAM' ) THEN 'true' ELSE 'false' END AS is_oper_team
, A.MEMBER_CD member_cd
A.EMP_NO emp_no , A.EMP_NM emp_nm , IFNULL(C.ORGN_CD,'800000') orgn_cd , IFNULL(C.ORGN_NM,'협력사') orgn_nm , A.SEARCHABLE_AUTH_CD searchable_auth_cd , 'P' board_publish_scope_part , TSMC.CUSTMR_GRUP board_publish_scope_cd , TSMC.CUSTMR_CD custmr_cd , TSMC.CUSTMR_NM custmr_nm , TSMC.CUSTMR_CEONM custmr_ceonm , CASE WHEN A.EMAIL IS NULL OR A.EMAIL = '' THEN NULL ELSE A.EMAIL END AS email , A.MBTLNUM AS mbtlnum
A.EMP_NO emp_no , A.EMP_NM emp_nm , IFNULL(C.ORGN_CD,'900000') orgn_cd , IFNULL(C.ORGN_NM,'고객사') orgn_nm , A.SEARCHABLE_AUTH_CD searchable_auth_cd , 'S' board_publish_scope_part , TSMC.CUSTMR_GRUP board_publish_scope_cd , TSMC.CUSTMR_CD custmr_cd , TSMC.CUSTMR_NM custmr_nm , TSMC.CUSTMR_CEONM custmr_ceonm , CASE WHEN A.EMAIL IS NULL OR A.EMAIL = '' THEN NULL ELSE A.EMAIL END AS email , A.MBTLNUM AS mbtlnum
FROM
COM_EMP_INFO A LEFT JOIN COM_ORGN_INFO C ON A.DEPT_CD=C.ORGN_CD LEFT JOIN COM_AUTHOR_BY_USER CA ON A.EMP_NO = CA.USER_CD WHERE A.EMP_NO=#{emp_no}
COM_EMP_INFO A LEFT JOIN COM_ORGN_INFO C ON A.DEPT_CD=C.ORGN_CD LEFT JOIN COM_AUTHOR_BY_USER CA ON A.EMP_NO = CA.USER_CD INNER JOIN T_STD_MAST_CUSTMR TSMC ON A.EMP_NO = TSMC.CUSTMR_CD WHERE TSMC.CUSTMR_CD=#{emp_no}

mapper.insert:
/* insertLoginTryLog 로그인 시도 로그 등록 */
INSERT INTO COM_LOGIN_ATPT_DTLS (
	EMP_NO
	, LOGIN_ID
	, LOGIN_ATPT_DT
	, LOGIN_ATPT_IP
	, IN_OUT
	, SUCCES_YN
	, RETURN_CD
	, REG_PROGRM
	, REG_DT
	, REG_ID
) VALUES (
	#{emp_no}
	,#{login_id}
	,NOW()
	,#{login_atpt_ip}
	,#{inOut}

	, 'Y'


	, 'N'

	, #{result_cd}
	, 'insertLoginAtptLog'
	, NOW()
	, 'SYSTEM'
)

mapper.update:
/* updateLoginPasswordErrorTime 패스워드 오류 횟수 업데이트 */
UPDATE COM_EMP_ACNT SET 
	PASSWORD_ERROR_TIME=#{password_error_time}
	, UPD_PROGRM='updateLoginPasswordErrorTime'
	, UPD_ID=#{emp_no}
	, UPD_DT=NOW()
WHERE EMP_NO=#{emp_no}

/* updatePassword 비밀번호 변경 */
UPDATE COM_EMP_ACNT SET 
	PASSWORD=#{CHANGE_PASSWORD}
	, PASSWORD_CHANGE_DT=NOW()
	, PASSWORD_ERROR_TIME=0		

		, INITL_YN='Y'
		, INITL_DT=NOW()


		, INITL_YN='N'

	, BEFORE_PASSWORD=PASSWORD
	, UPD_PROGRM='updatePassword'
	, UPD_ID=#{REG_ID}
	, UPD_DT=NOW()
WHERE EMP_NO=#{EMP_NO}

mapper.select:
/* getEmpNo */
SELECT 
	EA.EMP_NO
	,EA.ID 
FROM COM_EMP_ACNT EA
LEFT JOIN COM_EMP_INFO EI
	ON EA.EMP_NO = EI.EMP_NO
WHERE EA.EMP_NO = #{EMP_NO} 
	AND EI.EMP_NO = #{EMP_NO}

/* getLoginChangeCheckCnt */ SELECT SUM(CNT) FROM ( SELECT COUNT(E.MENU_CD) CNT FROM COM_EMP_INFO A JOIN COM_AUTHOR_BY_USER B ON A.EMP_NO=B.USER_CD AND B.USE_YN='Y' AND CONVERT(VARCHAR(8),NOW(), 112) BETWEEN B.BEGIN_DATE AND B.END_DATE JOIN COM_AUTHOR C ON B.AUTHOR_CD=C.AUTHOR_CD AND C.USE_YN='Y' AND CONVERT(VARCHAR(8),NOW(), 112) BETWEEN C.BEGIN_DATE AND B.END_DATE JOIN COM_AUTHOR_BY_MENU D ON B.AUTHOR_CD=D.AUTHOR_CD AND D.USE_YN='Y' JOIN COM_MENU E ON D.MENU_CD=E.MENU_CD AND E.USE_YN='Y' WHERE A.EMP_NO=#{EMP_NO_REAL} AND E.MENU_CD = '00023'
UNION ALL
SELECT COUNT(EMP_NO_LOGIN_ADD) CNT FROM COM_EMP_LOGIN_ADD WHERE EMP_NO=#{EMP_NO_REAL} OR EMP_NO_LOGIN_ADD = #{EMP_NO} ) A

/* getLoginChangeCheckAuth */
SELECT 
	COUNT(*) 
FROM COM_EMP_LOGIN_ADD
WHERE EMP_NO = #{EMP_NO} 
	AND EMP_NO_LOGIN_ADD = #{EMP_NO_LOGIN_ADD}

mapper.update:
/* updateMemberInPhone */
UPDATE MEMBER_INF SET 
	Member_InPhone = #{member_InPhone}
WHERE Member_Code = #{member_cd}
