mapper.select:
/* getOnlineOrderList 온라인 주문서 조회 */ SELECT ORD_CD ,ORD_NO ,ORD_SEQ ,OUT_WARE_CD ,T3.GOODS_NO ,T3.BCD_NM ,T3.BCD_CD ,ORD_QTY ,SALE_PRICE ,IFNULL(T1.SALE_VAT,'0') AS SALE_VAT ,SALE_TOT_PRICE ,DELI_YN ,DELI_PRICE ,IFNULL(T1.DELI_PRICE,'0') AS DELI_PRICE ,DELI_PRICE_TYPE ,CONVERT(NVARCHAR(21), [PAY_DATE], 21) AS [PAY_DATE] ,IFNULL(CONVERT(NVARCHAR(10),CONVERT(DATE,RESV_DATE),120),'') AS RESV_DATE ,IFNULL(CONVERT(NVARCHAR(10),CONVERT(DATE,SALE_DATE),120),'') AS SALE_DATE ,T1.CUSTMR_CD,T2.CUSTMR_NM,BUY_NM,BUY_TEL,BUY_PHONE,SHIPPER_NM,SHIPPER_TEL,SHIPPER_PHONE,SHIPPER_ZIP_NO,SHIPPER_ADDR ,DELI_NO,DELI_MEMO,DELI_PAY_TYPE,SC_S_QTY,SC_M_QTY,SC_L_QTY,ORDER_TYPE,ORD_CENT_CD,WMS_STATE,PRINT_YN,PUR_CONF_STATE,T1.CPROGRM,T1.CUSER ,CONVERT(NVARCHAR(21), T1.CDATE, 21) AS CDATE ,T1.MUSER ,CONVERT(NVARCHAR(21), T1.MDATE, 21) AS MDATE ,(ORD_CD + '_' + ORD_NO + '_' + SALE_DATE + '_' + RESV_DATE) AS UNIQUE_KEY FROM T_SALE_ONLINE_ORD T1 INNER JOIN T_STD_MAST_CUSTMR T2 ON T1.CUSTMR_CD = T2.CUSTMR_CD INNER JOIN T_STD_MAST_BCD T3 ON T1.BCD_CD = T3.BCD_CD WHERE 1=1 AND T1.WMS_STATE != '06' AND T1.ORDER_TYPE = #{ORDER_TYPE}
AND OUT_WARE_CD = #{ORGN_CD}
AND CONVERT(NVARCHAR(10),CONVERT(DATE,SALE_DATE),120) CONVERT(NVARCHAR(10),CONVERT(DATE,RESV_DATE),120) BETWEEN #{DATE_FR} AND #{DATE_TO} order by SALE_DATE

mapper.insert:
/* insertOnlineOrderList 임시테이블 저장 프로시저'C'*/
INSERT INTO T_SALE_ONLINE_ORD_TMP (
