mapper.delete:
/* deleteOnlineOrderList 선택 로우 삭제*/
DELETE FROM T_SALE_ONLINE_ORD 
WHERE
	ORD_CD = #{ORD_CD}
	AND ORD_NO = #{ORD_NO}
	AND ORD_SEQ = #{ORD_SEQ}
	AND SALE_DATE = CONVERT(nvarchar(8),CONVERT(datetime,#{SALE_DATE}),112)

mapper.select:
DECLARE @I_CRUD						NVARCHAR(1)				= #{CRUD}
DECLARE @I_PURCONF					NVARCHAR(MAX)			= #{PUR_CONF_STATE_LIST_STRING}
DECLARE @I_ORD_CD					NVARCHAR(MAX)			= #{ORD_CD_LIST_STRING}
DECLARE @I_ORD_NO					NVARCHAR(MAX)			= #{ORD_NO_LIST_STRING}
DECLARE @PARAM_SALEDATE				NVARCHAR(MAX)			= #{SALE_DATE_LIST_STRING}
DECLARE @PARAM_RESVDATE				NVARCHAR(MAX)			= #{RESV_DATE_LIST_STRING}

DECLARE @V_RETURN					INT						-- 리턴값 정상 :0, 비정상 : -1

SET @V_RETURN = 0

EXEC [dbo].[SP_ONLINE_ORD_UPDATE] 

@I_CRUD, @I_PURCONF, @I_ORD_CD, @I_ORD_NO, @PARAM_SALEDATE, @PARAM_RESVDATE, @V_RETURN output

SELECT @V_RETURN

/* getOnlineFixList 판매확정 헤더 조회 */ SELECT T1.ORGN_DIV_CD ,T1.ORGN_CD ,T1.ORD_CD ,T1.CUSTMR_CD ,T2.CUSTMR_NM ,T1.OUT_WARE_CD ,T1.ORD_DATE
,CASE WHEN COUNT(T4.BCD_CD) > 1 THEN CONCAT((SELECT BCD_NM FROM T_STD_MAST_BCD WHERE BCD_CD = MAX(T4.BCD_CD)),' 외 ',(COUNT(T4.BCD_CD)-1),'건') ELSE (SELECT BCD_NM FROM T_STD_MAST_BCD WHERE BCD_CD = MAX(T4.BCD_CD)) END AS TOT_GOODS_NM
,T1.[CURRCY_TYPE] ,T1.[ORD_TYPE] ,T1.[RESP_USER] ,T1.[REG_TYPE] ,T1.[SALE_VAT_AMT] ,T1.[SALE_AMT] ,T1.[SALE_TOT_AMT] ,T1.[CONF_AMT] ,IFNULL(T1.[CONF_TYPE],'N') AS CONF_TYPE ,IFNULL(T1.[SEND_TYPE],'N') AS SEND_TYPE ,IFNULL(T1.[OLINE_CONF_TYPE],'N') AS OLINE_CONF_TYPE ,T1.[CPROGRM] ,T1.[CUSER] ,T1.[CDATE] ,T1.[MUSER] ,T1.[MDATE] FROM T_SALE_ONLINE_MAST T1 INNER JOIN T_STD_MAST_CUSTMR T2 ON T1.CUSTMR_CD = T2.CUSTMR_CD INNER JOIN T_SALE_ONLINE_MAST_DETL T3 ON T1.ORD_CD = T3.ORD_CD AND T1.ORGN_DIV_CD = T3.ORGN_DIV_CD AND T1.ORGN_CD = T3.ORGN_CD INNER JOIN T_STD_MAST_BCD T4 ON T3.BCD_CD = T4.BCD_CD WHERE 1=1 AND ORD_DATE BETWEEN #{DATE_FR} AND #{DATE_TO}
GROUP BY T1.ORGN_DIV_CD ,T1.ORGN_CD ,T1.ORD_CD ,T1.CUSTMR_CD ,T2.CUSTMR_NM ,T1.OUT_WARE_CD ,T1.ORD_DATE ,T1.[CURRCY_TYPE] ,T1.[ORD_TYPE] ,T1.[RESP_USER] ,T1.[REG_TYPE] ,T1.[SALE_VAT_AMT] ,T1.[SALE_AMT] ,T1.[SALE_TOT_AMT] ,T1.[CONF_AMT] ,T1.[CONF_TYPE] ,T1.[SEND_TYPE] ,T1.[OLINE_CONF_TYPE] ,T1.[CPROGRM] ,T1.[CUSER] ,T1.[CDATE] ,T1.[MUSER] ,T1.[MDATE]

mapper.update:
/* updateOSFDList 판매확정 - 디테일 확정금액 업데이트*/
UPDATE T_SALE_ONLINE_MAST_DETL
	SET
		CONF_AMT	=	CAST(CAST(#{CONF_AMT} AS INT) AS decimal(19,6))
	WHERE
		ORD_CD		=	#{ORD_CD}
	AND
		BCD_CD		=	#{BCD_CD}

mapper.select:
DECLARE 	@PARAM_ORD_CD NVARCHAR(MAX) 
			, @PARAM_ORD_NO NVARCHAR(MAX)
			, @PARAM_SALE_DATE NVARCHAR(MAX)
			, @PARAM_RESV_DATE NVARCHAR(MAX)
			, @STD_DATE_TYPE NVARCHAR(2)
			, @DATE_FR NVARCHAR(8)
			, @DATE_TO NVARCHAR(8)

SET @PARAM_ORD_CD = #{ORD_CD_LIST}
SET @PARAM_ORD_NO = #{ORD_NO_LIST}
SET @PARAM_SALE_DATE = #{SALE_DATE_LIST}
SET @PARAM_RESV_DATE = #{RESV_DATE_LIST}
SET @STD_DATE_TYPE = #{STD_DATE_TYPE}
SET @DATE_FR = #{DATE_FR}
SET @DATE_TO = #{DATE_TO}

EXEC [dbo].[SP_WMS_SEND_ONLINE_ORD] @PARAM_ORD_CD, @PARAM_ORD_NO, @PARAM_SALE_DATE, @PARAM_RESV_DATE, @STD_DATE_TYPE, @DATE_FR, @DATE_TO
