mapper.update:
UPDATE TB_PRESET_MASTER 
SET  PRS_TITLE = #{prs_title}
  , PRS_YN = #{prs_yn}
  , PRS_EEMP = #{prs_eemp}
  , PRS_EDATE = NOW()
 WHERE PRS_CODE = #{prs_code}

UPDATE TB_PRESET_DETAIL 
SET PRSD_YN = #{prsd_yn}
  , PRSD_GOODS = #{prsd_goods}
  , PRSD_EEMP = #{prsd_eemp}
  , PRSD_EDATE = NOW()
 WHERE PRSD_CODE = #{prsd_code}

mapper.select:
/* getSalesByRealtimeList */ SELECT TSM.ORGN_DIV_CD ,TSM.ORGN_CD --조직코드 ,ORD_DATE -- 판매일자 ,CONVERT(NVARCHAR(16),ORD_DATE,8) ORD_DATE_TIME --판매시간 ,BILL_NO --영수증번호 ,SALE_TYPE --판매유형 ,REG_TYPE --판매상세 ,POS_NO --포스번호 ,CASE WHEN REG_TYPE = 'S02' THEN -SALE_AMT ELSE SALE_AMT END SALE_AMT --판매금액 ,ORD_CD --거래코드 ,IFNULL(TSM.MEM_NO,'') MEM_NO ,IFNULL(TSMM.MEM_NM,'') MEM_NM ,IFNULL(TSMM.UNION_CORP_MEM,'') UNION_CORP_MEM FROM T_SALE_MAST TSM LEFT JOIN ( SELECT ORGN_DIV_CD ,ORGN_CD ,MEM_NO ,MEM_NM ,CONCAT(CORP_NM,'[',MEM_NM,']') AS UNION_CORP_MEM FROM T_STD_MAST_MEM ) TSMM ON TSMM.ORGN_DIV_CD = TSM.ORGN_DIV_CD AND TSMM.ORGN_CD = TSM.ORGN_CD AND TSMM.MEM_NO = TSM.MEM_NO WHERE LEFT(ORD_DATE,10) = #{ORD_DATE}
AND TSM.ORGN_DIV_CD = #{ORGN_DIV_CD}
AND TSM.ORGN_CD = #{ORGN_CD}
AND TSM.ORGN_CD IS NOT NULL
AND POS_NO = #{POS_NO}
ORDER BY TSM.ORGN_CD, POS_NO, BILL_NO /* 시간이 정상적으로 들어올때 BILL_NO 대신 ORD_DATE_TIME 변경필요 */

/* getSalesByRealtimeDetailList */ SELECT TB.BCD_NM --상품명 ,CASE WHEN TS.REG_TYPE = 'S02' THEN -TSD.SALE_PRICE ELSE TSD.SALE_PRICE END SALE_PRICE --기준단가 ,CASE WHEN TS.REG_TYPE = 'S02' THEN -TSD.SALE_QTY ELSE TSD.SALE_QTY END SALE_QTY --수량 ,CASE WHEN TS.REG_TYPE = 'S02' THEN -TSD.SALE_QTY ELSE TSD.SALE_QTY END SALE_QTY --수량 ,CASE WHEN TS.REG_TYPE = 'S02' THEN -TSD.SALE_AMT ELSE TSD.SALE_AMT END SALE_AMT --금액 ,TS.ORD_CD --거래코드 ,CASE WHEN TS.REG_TYPE = 'S02' THEN -TSD.DC_AMT ELSE TSD.DC_AMT END DC_AMT --할인 FROM T_SALE_MAST_DETL AS TSD INNER JOIN T_SALE_MAST AS TS ON TSD.ORD_CD = TS.ORD_CD INNER JOIN T_STD_MAST_BCD AS TB ON TSD.BCD_CD = TB.BCD_CD WHERE TSD.ORD_CD = #{ORD_CD}
AND TSD.ORGN_CD = #{ORGN_CD}
AND TSD.ORGN_CD IS NOT NULL

mapper.update:
/* saveUpdateTrustSales */
UPDATE T_SALE_TRUST_INS
SET CASH_AMT = #{CASH_AMT}
	,CARD_AMT = #{CARD_AMT}
	,ACNT_AMT = #{ACNT_AMT}
	,PAY_DATE = #{PAY_DATE}
	,PAY_HOUR = #{PAY_HOUR}
	,PAY_MINUTE = #{PAY_MINUTE}
	,LOSS_AMT = #{LOSS_AMT}
	,SUM_AMT = #{SUM_AMT}
	,MEMO = #{MEMO}
WHERE LOAN_CD = #{LOAN_CD}
AND TRUST_SEQ = #{TRUST_SEQ}
AND OBJ_CD = #{OBJ_CD}

/* saveUpdateTrustSales */
DECLARE @INDE_AMT DECIMAL(19, 6)	/* 증감금액 */

SET @INDE_AMT = (
					SELECT INDE_AMT
					FROM T_STD_MAST_CREDITLOAN
					WHERE LOAN_SEQ = #{LOAN_SEQ}
					AND LOAN_CD = #{LOAN_CD}
				)

UPDATE T_SALE_TRUST_INS
SET DEL_FLAG ='Y' 
WHERE LOAN_CD = #{LOAN_CD}
AND TRUST_SEQ = #{TRUST_SEQ}
AND OBJ_CD = #{OBJ_CD}

mapper.select:
/* saveProAddTrustSales */
DECLARE @TRUST_SEQ BIGINT
		, @V_RETURN INT

SET @V_RETURN = 0
SET @TRUST_SEQ = (	SELECT IFNULL(MAX(LOAN_SEQ),1) AS TRUST_SEQ
					FROM T_STD_MAST_CREDITLOAN
					WHERE LOAN_CD = #{LOAN_CD}
				)

EXEC [dbo].[SP_TRUST_MEM_IN_MONEY]
	#{LOAN_CD}
	, #{OBJ_CD}
	, @TRUST_SEQ
	, @V_RETURN output

SELECT @V_RETURN

/* getsalesByStoreHeaderList 판매현황조회 */ SELECT TSM.ORGN_DIV_CD ORGN_DIV_CD --법입구분 , TSM.ORGN_CD ORGN_CD --조직명 ,CONVERT(NVARCHAR(10),TSM.ORD_DATE) AS ORD_DATE ,CASE WHEN COUNT(TSD.BCD_CD) > 1 THEN CONCAT((SELECT BCD_NM FROM T_STD_MAST_BCD WHERE BCD_CD = MAX(TSD.BCD_CD)),' 외 ',(COUNT(TSD.BCD_CD)-1),'건') ELSE (SELECT BCD_NM FROM T_STD_MAST_BCD WHERE BCD_CD = MAX(TSD.BCD_CD)) END AS TOT_BCD_NM ,SUM_SALE_TOT_AMT SUM_SALE_TOT_AMT ,SUM_SALE_AMT SUM_SALE_AMT ,SUM_PAY_CASH SUM_PAY_CASH --지불(현금) ,SUM_PAY_CARD SUM_PAY_CARD --지불(카드) ,SUM_PAY_GIFT SUM_PAY_GIFT --지불(상품권) ,SUM_PAY_POINT SUM_PAY_POINT --지불(포인트) ,SUM_PAY_TRUST SUM_PAY_TRUST --지불(외상) ,SUM_DISC_AMT SUM_DISC_AMT --금액(할인)
,SALE_TYPE
,REG_TYPE
,ORD_TYPE
FROM ( SELECT SM.ORGN_DIV_CD ,SM.ORGN_CD ,SM.ORD_DATE ,SUM(SM.SALE_TOT_AMT) AS SUM_SALE_TOT_AMT ,SUM(SM.SALE_AMT) AS SUM_SALE_AMT ,SUM(SM.PAY_CASH) AS SUM_PAY_CASH ,SUM(SM.PAY_CARD) AS SUM_PAY_CARD ,SUM(SM.PAY_GIFT) AS SUM_PAY_GIFT ,SUM(SM.PAY_POINT) AS SUM_PAY_POINT ,SUM(SM.PAY_TRUST) AS SUM_PAY_TRUST ,SUM(SM.CUTO_AMT) AS SUM_CUTO_AMT ,SUM(SM.DISC_AMT) AS SUM_DISC_AMT
,SM.SALE_TYPE
,SM.REG_TYPE
,SM.ORD_TYPE
FROM ( SELECT A.ORGN_DIV_CD ,A.ORGN_CD ,LEFT(ORD_DATE,10) AS ORD_DATE ,CASE WHEN REG_TYPE = 'S02' --반품일 때 마이너스 처리 THEN -A.SALE_TOT_AMT ELSE A.SALE_TOT_AMT END SALE_TOT_AMT ,CASE WHEN REG_TYPE = 'S02' THEN -A.SALE_AMT ELSE A.SALE_AMT END SALE_AMT ,CASE WHEN REG_TYPE = 'S02' THEN -PAY_CASH ELSE PAY_CASH END PAY_CASH ,CASE WHEN REG_TYPE = 'S02' THEN -PAY_CARD ELSE PAY_CARD END PAY_CARD ,CASE WHEN REG_TYPE = 'S02' THEN -PAY_GIFT ELSE PAY_GIFT END PAY_GIFT ,CASE WHEN REG_TYPE = 'S02' THEN -PAY_POINT ELSE PAY_POINT END PAY_POINT ,CASE WHEN REG_TYPE = 'S02' THEN -PAY_TRUST ELSE PAY_TRUST END PAY_TRUST ,CASE WHEN REG_TYPE = 'S02' THEN -CUTO_AMT ELSE CUTO_AMT END CUTO_AMT ,CASE WHEN REG_TYPE = 'S02' THEN -DISC_AMT ELSE DISC_AMT END DISC_AMT
,SALE_TYPE
,REG_TYPE
,ORD_TYPE
FROM T_SALE_MAST A INNER JOIN (SELECT DISTINCT ORD_CD FROM T_SALE_MAST_DETL) B ON A.ORD_CD = B.ORD_CD ) SM WHERE 1=1
AND SM.ORGN_DIV_CD = #{ORGN_DIV_CD}
AND SM.ORGN_CD = #{ORGN_CD}
AND SM.SALE_TYPE = #{SALE_TYPE}
AND SM.REG_TYPE = #{REG_TYPE}
AND SM.ORD_TYPE = #{ORD_TYPE}
AND SM.ORD_DATE BETWEEN #{DATE_FR} AND #{DATE_TO} GROUP BY SM.ORGN_DIV_CD ,SM.ORGN_CD ,SM.ORD_DATE
,SM.SALE_TYPE
,SM.REG_TYPE
,SM.ORD_TYPE
) TSM INNER JOIN (SELECT A.BCD_CD ,A.ORGN_DIV_CD ,A.ORGN_CD ,A.ORD_CD ,B.ORD_DATE FROM T_SALE_MAST_DETL A INNER JOIN ( SELECT ORGN_DIV_CD ,ORGN_CD,ORD_CD ,LEFT(ORD_DATE,10) AS ORD_DATE FROM T_SALE_MAST ) B ON A.ORGN_DIV_CD = B.ORGN_DIV_CD AND A.ORGN_CD = B.ORGN_CD AND A.ORD_CD = B.ORD_CD WHERE A.ORD_CD IN (SELECT ORD_CD FROM T_SALE_MAST SM WHERE 1=1
AND B.ORGN_DIV_CD = #{ORGN_DIV_CD}
AND B.ORGN_CD = #{ORGN_CD}
AND SM.SALE_TYPE = #{SALE_TYPE}
AND SM.REG_TYPE = #{REG_TYPE}
AND SM.ORD_TYPE = #{ORD_TYPE}
AND B.ORD_DATE BETWEEN #{DATE_FR} AND #{DATE_TO}) ) TSD ON TSM.ORGN_DIV_CD = TSD.ORGN_DIV_CD AND TSM.ORGN_CD = TSD.ORGN_CD AND TSM.ORD_DATE = TSD.ORD_DATE INNER JOIN (SELECT BCD_CD, BCD_NM FROM T_STD_MAST_BCD) TSMB ON TSD.BCD_CD = TSMB.BCD_CD
GROUP BY TSM.ORGN_DIV_CD , TSM.ORGN_CD , TSM.ORD_DATE ,SUM_SALE_TOT_AMT ,SUM_SALE_AMT ,SUM_PAY_CASH ,SUM_PAY_CARD ,SUM_PAY_GIFT ,SUM_PAY_POINT ,SUM_PAY_TRUST ,SUM_DISC_AMT
,SALE_TYPE
,REG_TYPE
,ORD_TYPE
ORDER BY TSM.ORD_DATE DESC

/* getsalesByStoreDetailList 판매현황조회*/ SELECT CONVERT(NVARCHAR(10),TSM.ORD_DATE) AS ORD_DATE ,TSM.ORGN_CD ,TB.BCD_NM ,TSM.BILL_NO ,TSM.ORD_TYPE ,TSM.SALE_TYPE ,TSM.POS_NO ,IFNULL(TSM.REG_TYPE,'없음') AS REG_TYPE ,TSM.ORD_CD ,CASE WHEN TSM.REG_TYPE = 'S02' THEN -TSD.SALE_PRICE WHEN TSM.REG_TYPE = 'S03' THEN -TSD.SALE_PRICE ELSE TSD.SALE_PRICE END SALE_PRICE ,CASE WHEN TSM.REG_TYPE = 'S02' THEN -TSD.SALE_QTY WHEN TSM.REG_TYPE = 'S03' THEN -TSD.SALE_QTY ELSE TSD.SALE_QTY END SALE_QTY ,CASE WHEN TSM.REG_TYPE = 'S02' THEN -TSD.SALE_AMT WHEN TSM.REG_TYPE = 'S03' THEN -TSD.SALE_AMT ELSE TSD.SALE_AMT END SALE_AMT ,CASE WHEN TSM.REG_TYPE = 'S02' THEN -TSD.SALE_VAT_AMT WHEN TSM.REG_TYPE = 'S03' THEN -TSD.SALE_VAT_AMT ELSE TSD.SALE_VAT_AMT END SALE_VAT_AMT ,CASE WHEN TSM.REG_TYPE = 'S02' THEN -TSD.SALE_TOT_AMT WHEN TSM.REG_TYPE = 'S03' THEN -TSD.SALE_TOT_AMT ELSE TSD.SALE_TOT_AMT END SALE_TOT_AMT ,TSD.DC_AMT FROM T_SALE_MAST_DETL TSD INNER JOIN (SELECT LEFT(ORD_DATE,10) ORD_DATE ,ORGN_DIV_CD ,ORGN_CD ,BILL_NO ,ORD_TYPE ,SALE_TYPE ,POS_NO ,REG_TYPE ,ORD_CD FROM T_SALE_MAST ) TSM ON TSD.ORD_CD = TSM.ORD_CD AND TSM.ORGN_DIV_CD = TSD.ORGN_DIV_CD AND TSM.ORGN_CD = TSD.ORGN_CD INNER JOIN T_STD_MAST_BCD AS TB ON TSD.BCD_CD = TB.BCD_CD WHERE TSD.ORD_CD IN (SELECT ORD_CD FROM T_SALE_MAST SM WHERE 1=1 AND TSM.ORGN_DIV_CD = #{ORGN_DIV_CD} AND TSM.ORGN_CD = #{ORGN_CD}
AND SM.SALE_TYPE = #{SALE_TYPE}
AND SM.REG_TYPE = #{REG_TYPE}
AND SM.ORD_TYPE = #{ORD_TYPE}
AND TSM.ORD_DATE = #{ORD_DATE} ) ORDER BY BILL_NO ASC
